catalog_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
   var urlParams = new URLSearchParams(top.window.location.search);
    var sysId = urlParams.get('sys_id');
    var businessApplication = urlParams.get('business_application');
    var requestFor = urlParams.get('request_for');

    g_form.setValue('business_application', businessApplication);
    g_form.setValue('request_for', requestFor);

    var ga = new GlideAjax('ReqItemDataFetcher');
    ga.addParam('sysparm_name', 'getdata'); 
    ga.addParam('sysparm_business_application', businessApplication);
    ga.addParam('sysparm_request_for', requestFor);
    ga.getXMLAnswer(parseResponse);

    function parseResponse(answer) {
        // alert('parseResponse'+answer); 
        g_form.setValue('comments', answer);
    }
}
