catalog_script_client.script:
function onLoad() { 
    // Retrieve the request item number from URL parameters
    var requestNumber = getUrlParameter('req_num'); 
    if (!requestNumber) return;

    // Create a GlideAjax object to call the server-side script
    var ajax = new GlideAjax('RequestDataHandler'); 
    // Set the name of the server-side method to be called
    ajax.addParam('sysparm_name', 'fetchRequestDetails'); 
    // Pass the request item number parameter
    ajax.addParam('sysparm_number', requestNumber); 
    // Send the request and process the response
    ajax.getXMLAnswer(processResponse); 
}

/**
 * Extract the value of a specified parameter from the URL
 */
function getUrlParameter(paramName) { 
    var currentUrl = top.location;
    // Escape special characters in the parameter name for regex construction
    paramName = paramName.replace(/[\[\]]/g, '\\$&'); 
    // Construct a regular expression to match the parameter in the URL
    var parameterRegex = new RegExp('[?&]' + paramName + '=([^&#]*)|&|$'); 
    var matchResults = parameterRegex.exec(currentUrl);
    // Decode and return the URL parameter value
    return decodeURIComponent(matchResults[1].replace(/\+/g, '')); 
}

/**
 * Process the Ajax response and populate form fields
 */
function processResponse(response) { 
    // Parse the JSON response into a JavaScript object
    var parsedData = JSON.parse(response); 
    if (parsedData) {
        for (var field in parsedData) {
            // Set the data to the form field
            g_form.setValue(field, parsedData[field]); 
        }
    }
}
