catalog_script_client.script:
/**
 * Handles form submission to update request item data via AJAX
 * 处理表单提交，通过AJAX更新请求项数据
 */
function onSubmit() {
    // Retrieve the request item number from URL parameters
    var requestNumber = getUrlParameter('req_num'); 

    if(!requestNumber) {
        return;
    }

    // Define all question fields to process
    // 定义所有要处理的问题字段
    var questionFields = ["business_application", "request_for", "comment"];
    var formData = { req_num: requestNumber };

    // Collect all question values in a loop
    // 循环收集所有问题值
    questionFields.forEach(function(field) {
        formData[field] = g_form.getValue(field);
    });

    // Create new AJAX request processor
    // 创建新的AJAX请求处理器
    var ajaxProcessor = new GlideAjax("UpdateRequestItem");
    ajaxProcessor.addParam("sysparm_name", "updateRequestItemData");

    // Add all parameters in a loop
    // 循环添加所有参数
    Object.keys(formData).forEach(function(key) {
        ajaxProcessor.addParam(key, formData[key]);
    });

    // Make the AJAX call
    // 发起AJAX调用
    ajaxProcessor.getXMLAnswer(handleSubmissionResponse); 
    return false; // Prevent default form submission / 阻止默认表单提交

    /**
     * Handles the response from the server after submission
     * 处理提交后来自服务器的响应
     * @param {string} response - The server response / 服务器响应
     */
    function handleSubmissionResponse(response) {
        if(response === "true") {
            // Redirect to index page on successful update
            // 更新成功后重定向到索引页面
            window.location.href = "sp?id=index";
        } else {
            // Handle error case here
            // 在此处理错误情况
            alert("Failed to update request item. Please try again.");
        }
    }
}

/**
 * 从 URL 中提取指定参数的值
 * Extract the value of a specified parameter from the URL
 */
function getUrlParameter(paramName) { 
    var currentUrl = top.location; // 获取当前页面的 URL
    // 转义参数名中的特殊字符以构建正则表达式
    // Escape special characters in the parameter name for regex construction
    paramName = paramName.replace(/[\[\]]/g, '\\$&'); 
    // 构建正则表达式匹配 URL 中的参数
    // Construct a regular expression to match the parameter in the URL
    var parameterRegex = new RegExp('[?&]' + paramName + '=([^&#]*)|&|$'); 
    var matchResults = parameterRegex.exec(currentUrl); // 执行正则匹配
    if (!matchResults) return null; // 如果未匹配到结果，返回 null
    if (!matchResults[1]) return ''; // 如果参数值为空，返回空字符串
    // 解码 URL 参数值并返回
    // Decode and return the URL parameter value
    return decodeURIComponent(matchResults[1].replace(/\+/g, '')); 
}
