catalog_script_client.script:
function onSubmit() {
	// Retrieve request item number from URL parameters
	var requestNumber = getUrlParameter('req_num'); 

	// Terminate submission if request number is empty
	if (!requestNumber) return;

	// Define array of form fields to collect
	var questionFields = ["business_application", "request_for", "comment"];
	var fieldCount = questionFields.length;
	var formData = { req_num: requestNumber };

	// Loop to collect form field values
	for (var i = 0; i < fieldCount; i++) {
		var field = questionFields[i];
		formData[field] = g_form.getValue(field) || '';
	}

	// Create GlideAjax object to call server-side script
	var ajaxProcessor = new GlideAjax("UpdateRequestItem");
	ajaxProcessor.addParam("sysparm_name", "updateRequestItemData");
	ajaxProcessor.addParam("sysparm_number", requestNumber);
	ajaxProcessor.addParam("sysparm_formData", JSON.stringify(formData));

	// Send asynchronous request and handle response (using anonymous callback)
	ajaxProcessor.getXMLAnswer(function(response) {

		// Success response handling (server returns "true")
		if (response === "true") {
			g_form.addInfoMessage('Success! Reloading now..');
			// Force page refresh after 2 seconds (clear beforeunload event to avoid popups)
			setTimeout(function() {
				top.window.onbeforeunload = null;
				top.window.location.reload(true);
			}, 2000);
		}
		// Failure response handling
		else {
			// Construct error message (prioritize server-provided error)
			var errorMsg = 'Update failed: ' + (response.message || 'Unknown error');
			g_form.addErrorMessage(errorMsg);

			// Refresh page after 3 seconds (restore page state after error)
			setTimeout(function() {
				top.window.onbeforeunload = null;
				top.window.location.reload(true);
			}, 3000);
		}	
		return false;
	});
	return false; 
}

/**
 * URL parameter retrieval method
 */
function getUrlParameter(paramName) { 
    var url = top.location; 
    paramName = paramName.replace(/[\[\]]/g, "\\$&"); 
    var regex = new RegExp("[?&]" + paramName + "=([^&#]*)");
    var results = regex.exec(url);
    if (!results || !results[1]) return '';
    return results === null ? "" : decodeURIComponent(results[1].replace(/\+/g, " "));
}
