sys_script_include.script:
var UpdateRequestItem = Class.create();

UpdateRequestItem.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    /**
     * Updates request item data with provided question answers
     * 使用提供的问题答案更新请求项数据
     * @return {string} "true" if successful / 成功返回"true"
     */
    updateRequestItemData: function() {
        // Get request number
        // 获取请求编号
        var requestNumber = this.getParameter("req_num");
        if(!requestNumber) {
            return "false"; // 添加：缺少必要参数时的返回
        }

        // Retrieve the request item record
        // 检索请求项记录
        var requestItem = new GlideRecord("sc_req_item");
        requestItem.addQuery("number", requestNumber);
        requestItem.query();

        if(requestItem.next()) {        
            // Define all fields to process
            // 定义所有要处理的问题字段
            var questionFields = ["business_application", "request_for", "comment"];

            // Update all fields in a loop
            // 循环更新所有问题字段
            for (var i = 0; i < questionFields.length; i++) {
                var field = questionFields[i];
                var value = this.getParameter(field);
                if(value !== null) {
                    requestItem.variables[field] = value;
                }
            }

            // Update standard fields
            // 更新标准字段
            requestItem.approval = "requested";
            requestItem.state = -192; // Assuming -192 is a valid state code / 假设-192是有效状态码

            // Save changes to database
            // 将更改保存到数据库
            return requestItem.update() ? "true" : "false";
        }
        return "false"; // 添加：记录未找到时的返回
    },
    type: 'UpdateRequestItem'
});
