sys_script_include.script:
var UpdateRequestItem = Class.create();

UpdateRequestItem.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    updateRequestItemData: function() {
        try {
            var requestNumber = this.getParameter("sysparm_number");

            if (!requestNumber) return;

			var formDataString = this.getParameter("sysparm_formData");
			gs.info("Received formData: " + formDataString);
			var formDataObj = JSON.parse(formDataString);

            alert("[Server] Processing request: " + requestNumber); // Debug
            var requestItem = new GlideRecord("sc_req_item");
            requestItem.addQuery("number", requestNumber);
            requestItem.query();

            if (requestItem.next()) {
                alert("[Server] Found request item record"); // Debug
                requestItem.state = -192;
                // 定义要更新的字段
                var questionFields = ["business_application", "request_for", "comment"];
                var fieldCount = questionFields.length;

                // 使用 for 循环替代 forEach
                for (var i = 0; i < fieldCount; i++) {
                    var field = questionFields[i];
                    var value = formDataObj[field];
                    alert("[Server] Processing field: " + field + " = " + value); // Debug                 
                    if (value !== null) {
                        requestItem.variables[field] = value;
                    }
                }
			requestItem.update();
			return "success";              
            }
        } catch (e) {
            return e.message;
        }
    },
    type: 'UpdateRequestItem'
});
