sys_script_include.script:
var RequestDataHandler = Class.create(); 
RequestDataHandler.prototype = Object.extendsObject(AbstractAjaxProcessor, {
    fetchRequestDetails: function() { 
        // Get the request item number parameter passed from the frontend
        var reqNumber = this.getParameter('sysparm_number'); 
        // Create a record object to query the request item table
        var requestRecord = new GlideRecord('sc_req_item'); 
        if (requestRecord.get('number', reqNumber)) {
            var fieldData = {};
            for (var fieldName in requestRecord.variables) { 
                // Convert the variable value to a string and store it
                fieldData[fieldName] = requestRecord.variables[fieldName] + ''; 
            }
            // Return data in JSON format
            return JSON.stringify(fieldData); 
        }
    },
    type: 'RequestDataHandler'
});
